import React from 'react'
import axios from 'axios'
import { Link } from "react-router-dom";
import Background from '../assets/signup.jpg'

export default function Signup_fct() {
    let email = null
    let password = null
    function onPost(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/user/signup', { email, password })
          .then((result) => {
            //access the results here.... 
            console.log(result.data);
            window.location.href = '/loginfct'
        });
    }

    function onChangeEmail(e) {
        e.preventDefault()
        email = e.target.value
        console.log('onChange');
    }
    function onChangePassword(e) {
        e.preventDefault()
        password = e.target.value
        console.log('onChange');
    }

    return (
        <div style={{ backgroundImage: `url(${Background})`, backgroundSize: 'cover', height: '1000px', width:'100%', padding: '20%',  }}>
            <div style={{ backgroundColor: 'white', borderRadius: '10px'}}>
                <h2 style={{textAlign: 'center'}}>Like minded</h2>
            </div>
            <h3>Inscrivez vous</h3>
            <form onSubmit={onPost}>
                <input
                type="text"
                name="email"
                placeholder="email"
                value={email}
                onChange={onChangeEmail}
                style={{border: 'none', borderRadius: '5px', width: '30%', height: '30px'}}
                />
                <br/>
                <br/>
                <input
                type="text"
                name="password"
                placeholder="mot de passe"
                value={password}
                onChange={onChangePassword}
                style={{border: 'none', borderRadius: '5px', width: '30%', height: '30px'}}
                />
                <br/>
                <br/>
                <button type="sumbit" style={{ border: 'none', background: '#f5cda2', borderRadius: '5px'}} >Envoyer</button>
            </form>

            <Link to="/login">Je n'ai pas de compte</Link>
        </div>
    )
}
