import React from 'react'
import axios from 'axios'
import Background from '../assets/signup.jpg'
import { Link } from "react-router-dom";

export default function Login_fct() {
    let email = null
    let password = null
    function onPost(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/user/login', { email, password })
          .then((result) => {
            //access the results here.... 
            console.log(result.data);
            localStorage.setItem('token', result.data.token)
            localStorage.setItem('userId', result.data.userId)
            window.location.href = '/connecte'
          });
        localStorage.setItem('email', email)
        console.log(localStorage.getItem('email'));
    }

    function onChangeEmail(e) {
        e.preventDefault()
        email = e.target.value
        console.log('onChangeEmail');
    }
    function onChangePassword(e) {
        e.preventDefault()
        password = e.target.value
        console.log('onChangePassword');
    }

    return (
        <div style={{ backgroundImage: `url(${Background})`, backgroundSize: 'cover', height: '1000px', width:'100%', padding: '30%' }}>
            <div>
                <h2>Connectez-vous</h2>
                <form onSubmit={onPost}>
                    <input
                    type="text"
                    name="email"
                    placeholder="email"
                    value={email}
                    onChange={onChangeEmail}
                    />
                    <br/>
                    <br/>
                    <input
                    type="text"
                    name="password"
                    placeholder="mot de passe"
                    value={password}
                    onChange={onChangePassword}
                    />
                    <br/>
                    <br/>
                    <button type="sumbit" style={{ border: 'none', background: '#f5cda2', borderRadius: '5px'}} >Se connecter</button>
                </form>
                <Link to="/signupfct">J'ai un compte</Link>
            </div>
        </div>
    )
}
