import React from 'react'
import axios from 'axios'
import { Link } from "react-router-dom";
import Background from '../assets/signup.jpg'

export default function Signup_fct() {
    let email = null
    let password = null
    function onPost(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/user/signup', {  email, password })
          .then((result) => {
            //access the results here.... 
            console.log(result.data);
            localStorage.setItem('token', result.data.token)
            localStorage.setItem('userId', result.data.userId)
            window.location.href = '/login'
          });
        localStorage.setItem('email', email)
        console.log(localStorage.getItem('email'));
    }

    function onChangeEmail(e) {
        e.preventDefault()
        email = e.target.value
        console.log('onChange');
    }
    function onChangePassword(e) {
        e.preventDefault()
        password = e.target.value
        console.log('onChange');
    }

    return (
        <div style={{ backgroundImage: `url(${Background})`, backgroundSize: 'cover', height: '1000px', width:'100%' }}>

            <h2>Like minded</h2>
            <h3>Inscrivez vous</h3>
            <form onSubmit={onPost}>
                <input
                type="text"
                name="email"
                placeholder="email"
                value={email}
                onChange={onChangeEmail}
                />
                <br/>
                <br/>
                <input
                type="text"
                name="password"
                placeholder="mot de passe"
                value={password}
                onChange={onChangePassword}
                />
                <br/>
                <br/>
                <button type="sumbit" style={{ border: 'none', background: '#f5cda2', borderRadius: '5px'}} >Envoyer</button>
            </form>

            <Link to="/loginfct">Je n'ai pas de compte</Link>
        </div>
    )
}
