import React, { useEffect, useState } from 'react'
import './Test.css'
import { socket } from '../socket'

export default function Test() {
    useEffect(()=>{
        socket.on('returnMessage', data => {
            let li = document.createElement('li')
            let texte = document.createTextNode(data)
            li.appendChild(texte)
            let liste = document.getElementById('liste')
            liste.appendChild(li)
        })
    }, [])


    const [msg, setMsg] = useState('')

    function submitMessage(e) {
        e.preventDefault()
        // console.log(msg);
        socket.emit('sendMessage', msg)

    }

    return (
        <div class='messagerie'>
            <ul id="liste"></ul>

            <input type="text" id="msg" name="message" onChange={ (e)=>setMsg(e.target.value) }></input>
            <button type="button" onClick={(e)=>submitMessage(e)} className="btn">Envoyer</button>
        </div>
    )
}
